mapper.select:
select id, user_id, password, nickname, is_admin, created_at, is_withdraw
from user
where user_id = #{userId}
  and password = #{password}

select id, user_id, password, nickname, is_admin, created_at, is_withdraw
from user
where id = #{id}
  and password = #{password}

select count(id)
from user
where user_id = #{userId}

mapper.update:
update user
set password = #{password}, updated_at = #{updatedAt}
where id = #{id}
